import React, { useState } from 'react';
import './SlidingPage.css'; // Import your CSS file for styling

function SlidingPage() {
  const [isVisible, setIsVisible] = useState(false);

  const togglePage = () => {
    setIsVisible(!isVisible);
  };

  return (
    <div className={`sliding-page ${isVisible ? 'visible' : ''}`}>
      <div className="content">
        {/* Your page content goes here */}
        <h1>Sliding Page</h1>
        <p>This is a sliding page content.</p>
      </div>
      <button className="close-button" onClick={togglePage}>
        Close
      </button>
    </div>
  );
}

export default SlidingPage;
