import React, { useState } from 'react';
 import logo from './Images/check.jpg';
import './work.css';
import img from './Images/mobile-screen.png';
import Sidebar from './Toogle';

function Work() {
  const [currentScreen, setCurrentScreen] = useState(1);

  const handlePrevScreen = () => {
    setCurrentScreen(currentScreen - 1);
  };

  const handleNextScreen = () => {
    setCurrentScreen(currentScreen + 1);
  };

  return (
    <div>
      <section>
        <Sidebar />
        {currentScreen === 1 && (
          <div className='mobile-screen-content'>
            <div className='mobile-screen'>
              <img className='img-png' src={img} alt='Mobile Screen 1' />
              <p className='hello'>hello</p>
            </div>

            <div>
              <img className='the-story' src={logo} alt='React Logo' />
            </div>
          </div>
        )}

        {currentScreen === 2 && (
          <div className='mobile-screen-content'>
            <div className='mobile-screen'>
              <img className='img-png' src={img} alt='Mobile Screen 2' />

              <div className='ok'>
                <h2 className='k'>.The-Product:</h2>
                <p className='description'>
                  This website targets users like photographers to learn more
                  skills and for brands that want services for their products.
                </p>
              </div>

              <div className='second'>
                <h2 className='Pd'>.Project duration:</h2>
                <p className='description-2'>
                  The project spanned a period of two weeks, during which we
                  diligently worked to create an exceptional UI/UX design.
                </p>
              </div>
            </div>
          </div>
        )}

        <div>
          {currentScreen > 1 && (
            <button onClick={handlePrevScreen}>Previous Screen</button>
          )}
          {currentScreen < 2 && (
            <button onClick={handleNextScreen}>Next Screen</button>
          )}
        </div>
      </section>
    </div>
  );
}

export default Work;
