import React, { useState } from 'react';
import './work.css';
import img from './Images/mobile-screen.png';
import Sidebar from './Toogle';

function Work() {
  const [currentScreen, setCurrentScreen] = useState(0);

  const screens = [
    {
      title: 'Screen 1',
      content: 'Content for Screen 1',
      image: logo,
    },
    {
      title: 'Screen 2',
      content: 'Content for Screen 2',
      image: logo,
    },
    // Add more screens as needed
  ];

  const handleScreenChange = (screenIndex) => {
    setCurrentScreen(screenIndex);
  };

  return (
    <div>
      <section>
        <Sidebar />
        <div className='mobile-screen-content'>
          <div className='mobile-screen'>
            <img className='img-png' src={img} alt='Mobile Screen' />
            <p className='hello'>{screens[currentScreen].title}</p>
          </div>
          <div>
            <img
              className='the-story'
              src={screens[currentScreen].image}
              alt='React Logo'
            />
          </div>
        </div>
        <div className='mobile-screen-content'>
          <div className='mobile-screen'>
            <img className='img-png' src={img} alt='Mobile Screen' />
            <div className='ok'>
              <h1 className='head'>{screens[currentScreen].title}</h1>
              <p className='lol'>{screens[currentScreen].content}</p>
            </div>
          </div>
        </div>
        <button onClick={() => handleScreenChange((currentScreen + 1) % screens.length)}>Next Screen</button>
      </section>
    </div>
  );
}

export default Work;
