import React, { useState } from 'react';
import './Slideshow.css'; // Import your CSS file if needed

const Slideshow = ({ slides }) => {
  const [currentSlide, setCurrentSlide] = useState(0);

  const handlePrevSlide = () => {
    setCurrentSlide((prevSlide) => (prevSlide === 0 ? slides.length - 1 : prevSlide - 1));
  };

  const handleNextSlide = () => {
    setCurrentSlide((prevSlide) => (prevSlide === slides.length - 1 ? 0 : prevSlide + 1));
  };

  return (
    <div className="slideshow-wrapper">
      <div className="slideshow-gutter">
        <div className="slideshow-holder">
          <ul className="slides">
            {slides.map((slide, index) => (
              <li
                key={index}
                className={`slide list-item ${index === currentSlide ? 'active' : ''}`}
              >
                <div className="slide-media-container">
                  <img src={slide.imageSrc} alt={slide.title} />
                </div>
                <div className="slide-content list-item-card-background">
                  <h2 className="list-item-content__title">{slide.title}</h2>
                  <div className="list-item-content__button-container">
                    <a
                      className="list-item-content__button"
                      href={slide.link}
                      target="_blank"
                      rel="noopener noreferrer"
                    >
                      VIEW
                    </a>
                  </div>
                </div>
              </li>
            ))}
          </ul>
        </div>
        <button className="arrow-button arrow-button--left" onClick={handlePrevSlide}>
          {/* Left arrow SVG here */}
        </button>
        <button className="arrow-button arrow-button--right" onClick={handleNextSlide}>
          {/* Right arrow SVG here */}
        </button>
      </div>
    </div>
  );
};

export default Slideshow;
