import logo from './Images/check.jpg';
import './work.css';
import img from './Images/mobile-screen.png'

import Sidebar from './Toogle';
import { useState } from 'react';


function Work() {

  const[currentScreen,SetCurrentScreen]=useState(1);

  const handlePreviousScreen=()=>{
    SetCurrentScreen(currentScreen -1)

  }

  const handleNextScreen=()=>{
    SetCurrentScreen(currentScreen + 1)

  }




  return (<div>
    <section >
      <Sidebar />

      {
        currentScreen ===1 &&(
          <div className='mobile-screen-content'>
        <div className='mobile-screen'>
          <img className='img-png' src={img} />
          <p className='hello'>hello</p>
        </div>

        <div>
          <img className="the-story" src={logo} alt="React Logo" />
        </div>

      </div>
        )
      }



     {
      currentScreen ===2 &&(
        <div className='mobile-screen-content'>
        <div className='mobile-screen'>
          <img className='img-png' src={img} />

          <div className='ok'>
            <h2 className='k'>.The-Product:</h2>
            <p className='description'> This website Target userâ€™s like photographer to get
              to know more skills and learn &also for the brands
              that want services for there products </p>
          </div>


          <div className='second'>
            <h2 className='Pd'>.Project duration:</h2>
            <p className='description-2'>The project spanned a period of two weeks, during which
              we diligently worked to create an exceptional UI/UX design</p>
          </div>

        </div>
      </div>
      )
     }




    </section>

    <div>
        {
          currentScreen >1 &&(
            <button onClick={handlePreviousScreen} />
          )
        }

        {
          currentScreen <2 &&(
            <button onClick={handleNextScreen} />
          )
        }
      </div>

  </div>)
}

export default Work;
